import images from '../images'
const Header = () => {

    useEffect(()=> {
        function lerp(start, end, t){
            return start * (1 - t) + end * t;
        }

        //mouse cords
        let targetX = 0;
        let targetY = 0;

        //load image textures for mesh
        const textureOne = new THREE.TextureLoader().load(images.imageOne);
        const textureTwo = new THREE.TextureLoader().load(images.imageTwo);
        const textureThree = new THREE.TextureLoader().load(images.imageThree);

        class webgl{
            constructor(){
                this.container = this.container = document.querySelector('main');

            }
        }
        new webgl();
    })

    return (
        <main>
            <div className="hero">
                <div className="hero__title">
                    <h1>
                        <span className="hero__nine">NINE</span> 
                        <br/> 
                        <span className="hero__fifty">FIFTY</span>
                        <br/>
                        <span className="her__store">STORE</span>
                    </h1>
                    <p className="hero__logo">9 FIFTY</p>
                    <p className="hero__top-center">STREETWEAR CLOTHING</p>
                    <a href="" className="hero__about">ABOUT US</a>
                    <a href="" className="hero__shop">SHOP NOW</a>
                    <div className="circle">
                        <p>0</p>
                    </div>
                </div>
            </div>
        </main>
    )
}

export default Header
