import { useState } from 'react'
import Header from './components/Header';
import Shop from './components/Shop';
import About from './components/About';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'  
import Nav from './components/Nav'
import EachProduct from './components/EachProduct';

//images
import hoodieOne from './images/hoodie1.jpg'
import hoodieTwo from './images/hoodie2.jpg'
import teeOne from './images/tee1.jpg'
import teeTwo from './images/tee2.jpg'



function App() {
  const products = useState([
    {
        title: "CAMO HOODIE",
        tag: "Camo Hoodie",
        price: 90,
        img: hoodieOne,
        id: 1,
        quantity: 1
    },
    {
        title: "PRIDE HOODIE",
        tag: "Pride Hoodie",
        price: 80,
        img: hoodieTwo,
        id: 2,
        quantity: 1

    },
    {
        title: "CC TEE",
        tag: "CC Tee",
        price: 32,
        img: teeOne,
        id: 3,
        quantity: 1
    },
    {
        title: "KLEPTO TEE",
        tag: "Klepto Tee",
        price: 32,
        img: teeTwo,
        id: 4,
        quantity: 1
    },
])

  return (
    <Router>
      <div className="App">
      <Nav/>
      <Switch>
        <Route path="/" exact component={Header}/>
        <Route path="/about" component={About}/>
        <Route path="/shop" component={Shop} products={products}/>
        <Route path="/products/:title" component={EachProduct}/>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
